function write($what)
{
	if(file_exists('output.txt'))
		@unlink('output.txt');

	$file = @fopen('output.txt', 'w');
	
	if($file)
	{
		fwrite($file, $what);
		@fclose($file);
	}
	else
	{
		exit('NEED WRITE PERMISSION');
	}

}